import React, { useState, useEffect } from "react";
import { getJobs, createJob, deleteJob as apiDeleteJob } from "../api/api"; // make sure deleteJob exists in api
import "../styles/dashboard.css";

const Dashboard = ({ token, onLogout }) => {
  const [jobs, setJobs] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [company, setCompany] = useState("");

  // Fetch all jobs
  const fetchJobs = async () => {
    const data = await getJobs(token);
    setJobs(data);
  };

  // Create a new job
  const handleCreateJob = async (e) => {
    e.preventDefault();
    const data = await createJob({ title, description, company }, token);
    setJobs((prev) => [...prev, data]);
    setTitle("");
    setDescription("");
    setCompany("");
  };

  // Delete a job
  const handleDeleteJob = async (id) => {
    if (!window.confirm("Are you sure you want to delete this job?")) return;
    await apiDeleteJob(id, token); // call API to delete
    setJobs((prev) => prev.filter((job) => job._id !== id));
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  return (
    <div className="dashboard-container">
      <h2>Dashboard</h2>
      <button className="logout-btn" onClick={onLogout}>Logout</button>

      <h3>Create Job</h3>
      <form onSubmit={handleCreateJob} className="create-job-form">
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Company"
          value={company}
          onChange={(e) => setCompany(e.target.value)}
          required
        />
        <button type="submit">Add Job</button>
      </form>

      <h3>All Jobs</h3>
      <ul className="jobs-list">
        {jobs.map((job) => (
          <li key={job._id} className="job-item">
            <span>{job.title} - {job.company}</span>
            <button
              className="btn delete"
              onClick={() => handleDeleteJob(job._id)}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
