import React, { useState } from "react";
import Login from "./pages/Login";
import Dashboard from "./pages/Dashboard";

function App() {
  // Store JWT token in state
  const [token, setToken] = useState(localStorage.getItem("eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY4Zjc2NGEyYzE4ZmM4ZTI3OTQ2YWVkYiIsImlhdCI6MTc2MTA1MjA3NiwiZXhwIjoxNzYzNjQ0MDc2fQ.syLKD8ukKr1q8sJqKji88-cxicuxaiOqp2GJ87kbMA0") || null);

  return (
    <div className="App">
      {!token ? (
        <Login setToken={setToken} /> // Pass setToken to Login page
      ) : (
        <Dashboard token={token} /> // Pass token to protected pages
      )}
    </div>
  );
}

export default App;
