import React, { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [editingJobId, setEditingJobId] = useState(null);
  const [form, setForm] = useState({ title: "", company: "", description: "" });
  const token = localStorage.getItem("token"); // JWT token if using auth

  // Fetch all jobs
  const fetchJobs = async () => {
    try {
      const { data } = await axios.get("/api/jobs", {
        headers: token ? { Authorization: `Bearer ${token}` } : {},
      });
      setJobs(data);
    } catch (err) {
      console.error("Failed to fetch jobs", err);
    }
  };

  // Create a new job
  const createJob = async () => {
    try {
      if (!form.title || !form.company || !form.description) {
        alert("All fields are required");
        return;
      }
      const { data } = await axios.post("/api/jobs", form, {
        headers: token ? { Authorization: `Bearer ${token}` } : {},
      });
      setJobs([data, ...jobs]);
      setForm({ title: "", company: "", description: "" });
    } catch (err) {
      console.error("Failed to create job", err);
    }
  };

  // Delete a job
  const deleteJob = async (id) => {
    if (!window.confirm("Are you sure you want to delete this job?")) return;

    try {
      await axios.delete(`/api/jobs/${id}`, {
        headers: token ? { Authorization: `Bearer ${token}` } : {},
      });
      setJobs(jobs.filter((job) => job._id !== id));
    } catch (err) {
      console.error("Failed to delete job", err);
    }
  };

  // Start editing a job
  const startEditing = (job) => {
    setEditingJobId(job._id);
    setForm({ title: job.title, company: job.company, description: job.description });
  };

  const cancelEditing = () => {
    setEditingJobId(null);
    setForm({ title: "", company: "", description: "" });
  };

  // Save edited job
  const saveEdit = async (id) => {
    try {
      const { data } = await axios.put(`/api/jobs/${id}`, form, {
        headers: token ? { Authorization: `Bearer ${token}` } : {},
      });
      setJobs(jobs.map((job) => (job._id === id ? data : job)));
      cancelEditing();
    } catch (err) {
      console.error("Failed to update job", err);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">Job Dashboard</h1>

      {/* Create Job Form */}
      {editingJobId === null && (
        <div className="mb-6 p-4 border rounded bg-gray-50">
          <h2 className="text-xl font-semibold mb-2">Create New Job</h2>
          <div className="flex flex-col md:flex-row gap-2">
            <input
              className="border p-2 rounded flex-1"
              placeholder="Title"
              value={form.title}
              onChange={(e) => setForm({ ...form, title: e.target.value })}
            />
            <input
              className="border p-2 rounded flex-1"
              placeholder="Company"
              value={form.company}
              onChange={(e) => setForm({ ...form, company: e.target.value })}
            />
            <input
              className="border p-2 rounded flex-2"
              placeholder="Description"
              value={form.description}
              onChange={(e) => setForm({ ...form, description: e.target.value })}
            />
            <button
              onClick={createJob}
              className="bg-green-500 text-white px-4 py-2 rounded"
            >
              Add Job
            </button>
          </div>
        </div>
      )}

      {/* Jobs List */}
      <ul>
        {jobs.length === 0 && <p>No jobs available</p>}
        {jobs.map((job) => (
          <li
            key={job._id}
            className="flex flex-col md:flex-row justify-between items-start md:items-center bg-gray-100 p-3 mb-2 rounded"
          >
            {editingJobId === job._id ? (
              <div className="flex flex-col md:flex-row md:items-center gap-2 w-full">
                <input
                  className="border p-1 rounded"
                  value={form.title}
                  onChange={(e) => setForm({ ...form, title: e.target.value })}
                  placeholder="Title"
                />
                <input
                  className="border p-1 rounded"
                  value={form.company}
                  onChange={(e) => setForm({ ...form, company: e.target.value })}
                  placeholder="Company"
                />
                <input
                  className="border p-1 rounded"
                  value={form.description}
                  onChange={(e) => setForm({ ...form, description: e.target.value })}
                  placeholder="Description"
                />
                <button
                  className="bg-green-500 text-white px-3 py-1 rounded"
                  onClick={() => saveEdit(job._id)}
                >
                  Save
                </button>
                <button
                  className="bg-gray-500 text-white px-3 py-1 rounded"
                  onClick={cancelEditing}
                >
                  Cancel
                </button>
              </div>
            ) : (
              <div className="flex justify-between items-center w-full">
                <span>
                  {job.title} - {job.company}
                </span>
                <div className="flex gap-2">
                  <button
                    onClick={() => startEditing(job)}
                    className="bg-blue-500 text-white px-3 py-1 rounded"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => deleteJob(job._id)}
                    className="bg-red-500 text-white px-3 py-1 rounded"
                  >
                    Delete
                  </button>
                </div>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
