import React, { useState } from "react";
import { useNavigate } from "react-router-dom";


const Login = ({ onLogin }) => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // Predefined token for testing
  const predefinedToken =
    "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY4Zjc2NGEyYzE4ZmM4ZTI3OTQ2YWVkYiIsImVtYWlsIjoibmF2ZWVuc2FiYmF2YXJhcHUxMTFAZ21haWwuY29tIiwiaWF0IjoxNzYxMDQ4MTA5LCJleHAiOjE3NjExMzQ1MDl9.yN2nSAtp5xAEj2WjcS99ZrS_7IFB9ZLy7ALQyHCrnOw";

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Here you can call backend login if needed:
      // const data = await loginUser({ email, password });

      // For testing, use predefined token
      const data = { token: predefinedToken };

      if (data && data.token) {
        // Call onLogin prop if passed
        if (onLogin) onLogin(data.token);

        // Store token in localStorage
        localStorage.setItem("token", data.token);

        // Redirect to Dashboard
        navigate("/dashboard");
      } else {
        setError(data.message || "Login failed");
      }
    } catch (err) {
      console.error("Login error:", err);
      setError("Login failed. Check console.");
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "50px auto", textAlign: "center" }}>
      return <h1>Login Page Test</h1>;
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ padding: "10px", borderRadius: "6px", border: "1px solid #ccc" }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ padding: "10px", borderRadius: "6px", border: "1px solid #ccc" }}
        />
        <button
          type="submit"
          style={{
            padding: "10px",
            border: "none",
            borderRadius: "6px",
            backgroundColor: "#2575fc",
            color: "white",
            cursor: "pointer",
          }}
        >
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
