import { useState } from "react";
import API from "../api/api";

const ApplicationForm = ({ job, onClose }) => {
  const [resume, setResume] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.post("/applications", { job: job._id, resume });
      alert("Application submitted!");
      onClose();
    } catch (err) {
      alert(err.response?.data?.message || "Failed to apply");
    }
  };

  return (
    <div style={{ border: "1px solid #000", padding: "10px", marginTop: "10px" }}>
      <h4>Apply for: {job.title}</h4>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Resume URL" value={resume} onChange={(e) => setResume(e.target.value)} required />
        <button type="submit">Submit Application</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default ApplicationForm;
