import axios from "axios";

// Base URL for backend
const BASE_URL = "http://localhost:5000/api";

// Register user
export const registerUser = async (name, email, password) => {
  try {
    const response = await axios.post(`${BASE_URL}/auth/register`, { name, email, password });
    return response.data;
  } catch (error) {
    console.error("Registration failed:", error.response?.data || error.message);
    throw error;
  }
};

// Login user
export const loginUser = async (email, password) => {
  try {
    const response = await axios.post(`${BASE_URL}/auth/login`, { email, password });
    return response.data; // { message, token, user }
  } catch (error) {
    console.error("Login failed:", error.response?.data || error.message);
    throw error;
  }
};

// Get all jobs (example protected route)
export const getJobs = async (token) => {
  try {
    const response = await axios.get(`${BASE_URL}/jobs`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error("Fetching jobs failed:", error.response?.data || error.message);
    throw error;
  }
};

// Default export for your components
export default {
  registerUser,
  loginUser,
  getJobs,
};
