import React, { useState, useEffect } from "react";
import { getJobs, createJob, deleteJob as apiDeleteJob } from "../api/api";
import "../styles/dashboard.css";

const Dashboard = ({ onLogout }) => {
  // Read token from localStorage to prevent undefined errors
  const token = localStorage.getItem("token");

  const [jobs, setJobs] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [company, setCompany] = useState("");

  // Fetch jobs from backend
  const fetchJobs = async () => {
    try {
      const data = await getJobs(token);
      setJobs(data);
    } catch (err) {
      console.error("Error fetching jobs:", err);
      alert("Failed to fetch jobs. Check backend.");
    }
  };

  // Create a new job
  const handleCreateJob = async (e) => {
    e.preventDefault();
    try {
      const data = await createJob({ title, description, company }, token);
      setJobs((prev) => [...prev, data]);
      setTitle("");
      setDescription("");
      setCompany("");
    } catch (err) {
      console.error("Error creating job:", err);
      alert("Failed to create job. Check backend.");
    }
  };

  // Delete a job
  const handleDeleteJob = async (id) => {
    if (!window.confirm("Are you sure you want to delete this job?")) return;
    try {
      await apiDeleteJob(id, token);
      setJobs((prev) => prev.filter((job) => job._id !== id));
    } catch (err) {
      console.error("Error deleting job:", err);
      alert("Failed to delete job. Check backend logs.");
    }
  };

  useEffect(() => {
    if (token) fetchJobs();
  }, [token]);

  return (
    <div className="dashboard-container">
      {/* Header */}
      <div className="dashboard-header">
        <h1>Job Dashboard</h1>
        <button className="logout-btn" onClick={onLogout}>
          Logout
        </button>
      </div>

      {/* Create Job Form */}
      <div className="create-job-form">
        <h2>Create Job</h2>
        <form onSubmit={handleCreateJob}>
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Company"
            value={company}
            onChange={(e) => setCompany(e.target.value)}
            required
          />
          <button type="submit">Add Job</button>
        </form>
      </div>

      {/* Jobs List */}
      <h2>All Jobs</h2>
      <div className="jobs-grid">
        {jobs.map((job) => (
          <div className="job-card" key={job._id}>
            <h3>{job.title}</h3>
            <p className="company">{job.company}</p>
            <p className="description">{job.description}</p>
            <button
              className="btn delete"
              onClick={() => handleDeleteJob(job._id)}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
