import React from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();

  // Predefined token
  const token =
    "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY4Zjc2NGEyYzE4ZmM4ZTI3OTQ2YWVkYiIsImVtYWlsIjoibmF2ZWVuc2FiYmF2YXJhcHUxMTFAZ21haWwuY29tIiwiaWF0IjoxNzYxMDQ4MTA5LCJleHAiOjE3NjExMzQ1MDl9.yN2nSAtp5xAEj2WjcS99ZrS_7IFB9ZLy7ALQyHCrnOw";

  const handleLogin = () => {
    // Store token in localStorage
    localStorage.setItem("token", token);
    navigate("/dashboard");
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        background: "linear-gradient(135deg, #6a11cb, #2575fc)",
        color: "white",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <h1>Job Board Login</h1>
      <button
        onClick={handleLogin}
        style={{
          padding: "12px 20px",
          border: "none",
          borderRadius: "8px",
          backgroundColor: "#ff6a00",
          color: "white",
          fontSize: "16px",
          cursor: "pointer",
        }}
      >
        Login
      </button>
    </div>
  );
};

export default Login;
